import React, { useState } from "react";

const NavBar = () => {

  function Mostrar(){
    document.getElementById('menu').addEventListener('click', function() {
      var mobileMenu = document.getElementById('mobile-menu');
      if (mobileMenu.classList.contains('hidden')) {
        mobileMenu.classList.remove('hidden');
      } else {
        mobileMenu.classList.add('hidden');
      }
    });
  };

  return (
    <>
      <nav class="bg-principal-color flex items-center justify-between p-4">
 
      <div class="text-white text-xl font-bold">ProVision</div>
  
      <div class="hidden md:flex space-x-4">
        <a href="#" class="rounded-md px-3 py-2 text-sm font-medium text-white hover:text-white hover:bg-blue-500">Dashboard</a>
        <a href="#" class="rounded-md px-3 py-2 text-sm font-medium text-white hover:text-white hover:bg-blue-500">Team</a>
        <a href="#" class="rounded-md px-3 py-2 text-sm font-medium text-white hover:text-white hover:bg-blue-500">Projects</a>
        <a href="#" class="rounded-md px-3 py-2 text-sm font-medium text-white hover:text-white hover:bg-blue-500">Calendar</a>
      </div>
      
      <button id="menu" onClick={Mostrar}>
        <img class="h-8 w-8 rounded-full" src="https://thumbs.dreamstime.com/b/l%C3%ADnea-icono-del-negro-avatar-perfil-de-usuario-121102131.jpg" alt="Not found"/>
      </button>

      <div id="mobile-menu" class="inset-0 bg-principal-color flex flex-col items-center space-y-4 p-4 hidden">
        <p class="text-white text-sm font-medium hover:bg-blue-500 rounded-md px-4 py-2">Your Enterprise</p>
        
        <hr class="border-1 w-full"/>

        <a href="#" class="text-white text-sm font-medium hover:bg-blue-500 rounded-md px-4 py-2">Your Profile</a>
        <a href="#" class="text-white text-sm font-medium hover:bg-blue-500 rounded-md px-4 py-2">Settings</a>
        <a href="#" class="text-white text-sm font-medium hover:bg-blue-500 rounded-md px-4 py-2">Log out</a>
      </div>
    </nav>

    <div class="bg-principal-color">

    </div>

    </>
  );
};

export default NavBar;
