import React, { useState } from 'react';

const Navbar = () => {
  const [isOpen, setIsOpen] = useState(false);

  return (
    <>
      <nav className="flex flex-wrap items-center justify-between bg-principal-color h-24 px-6 lg:px-12">

        <p className="text-white text-2xl">ProVision</p>

        <div className="hidden lg:flex lg:flex-grow lg:items-center lg:w-auto">
          <div className="text-white space-x-8">
            <a href="#" className="text-white px-2 py-2 hover:bg-blue-600 hover:rounded-lg">Home</a>
            <a href="#" className="text-white px-2 py-2 hover:bg-blue-600 hover:rounded-lg">About us</a>
            <a href="#" className="text-white px-2 py-2 hover:bg-blue-600 hover:rounded-lg">Features</a>
            <a href="#" className="text-white px-2 py-2 hover:bg-blue-600 hover:rounded-lg">Contact us</a>
          </div>
        </div>

        <button
          className="lg:hidden"
          onClick={() => setIsOpen(!isOpen)}
        >
          <img className="h-10 w-10 rounded-full" src="https://thumbs.dreamstime.com/b/l%C3%ADnea-icono-del-negro-avatar-perfil-de-usuario-121102131.jpg" alt="No Image" />
        </button>

        {isOpen && (
          <div className="lg:hidden absolute top-24 left-0 w-full bg-principal-color">
            <div className="text-white flex flex-col items-center pt-4">
              <a href="#" className="text-white py-2 hover:bg-blue-600 hover:rounded-lg">Home</a>
              <a href="#" className="text-white py-2 hover:bg-blue-600 hover:rounded-lg">About us</a>
              <a href="#" className="text-white py-2 hover:bg-blue-600 hover:rounded-lg">Features</a>
              <a href="#" className="text-white py-2 hover:bg-blue-600 hover:rounded-lg">Contact us</a>
            </div>
          </div>
        )}

      </nav>
    </>
  );
};

export default Navbar;
