import React from "react";

const NavBar = () => {
  const mostrarMenu = () => {
    const mobileMenu = document.getElementById('mobile-menu');
    mobileMenu.classList.toggle('hidden');
  };

  const quitarMenu = () => {
    const mobileMenu = document.getElementById('mobile-menu');
    mobileMenu.classList.add('hidden');
  };

  return (
    <>
      <nav className="bg-principal-color flex items-center justify-between p-4 relative">
        <div className="text-white text-xl font-bold">ProVision</div>

        <div className="hidden md:flex space-x-4">
          <a href="#" className="rounded-md px-3 py-2 text-sm font-medium text-white hover:text-white hover:bg-blue-500">Dashboard</a>
          <a href="#" className="rounded-md px-3 py-2 text-sm font-medium text-white hover:text-white hover:bg-blue-500">Team</a>
          <a href="#" className="rounded-md px-3 py-2 text-sm font-medium text-white hover:text-white hover:bg-blue-500">Projects</a>
          <a href="#" className="rounded-md px-3 py-2 text-sm font-medium text-white hover:text-white hover:bg-blue-500">Calendar</a>
        </div>

        <div className="relative">
          <button id="menu" onClick={mostrarMenu} className="relative z-10">
            <img className="h-8 w-8 rounded-full" src="https://thumbs.dreamstime.com/b/l%C3%ADnea-icono-del-negro-avatar-perfil-de-usuario-121102131.jpg" alt="Not found"/>
          </button>

          <div id="mobile-menu" className="absolute top-full left-0 bg-principal-color flex flex-col items-center space-y-4 p-4 hidden rounded-lg z-20">
            <div className="space-x-3 p-3 flex items-center">
              <img className="h-8 w-8 rounded-full" src="https://thumbs.dreamstime.com/b/l%C3%ADnea-icono-del-negro-avatar-perfil-de-usuario-121102131.jpg" alt="Not found"/>
              <p className="text-white text-sm font-medium hover:bg-blue-500 rounded-md px-4 py-2">Your Enterprise</p>
              <button id="x" onClick={quitarMenu}>
                <p className="text-white text-xl">x</p>
              </button>
            </div>

            <hr className="border-1 w-full"/>

            <a href="#" className="text-white text-sm font-medium hover:bg-blue-500 rounded-md px-4 py-2">Your Profile</a>
            <a href="#" className="text-white text-sm font-medium hover:bg-blue-500 rounded-md px-4 py-2">Settings</a>
            <a href="#" className="text-white text-sm font-medium hover:bg-blue-500 rounded-md px-4 py-2">Log out</a>
          </div>
        </div>
      </nav>
    </>
  );
};

export default NavBar;
