import React, { useState } from 'react';

const Navbar = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [isOpen2, setIsOpen2] = useState(false);

  const mostrar = () => {
    setIsOpen(!isOpen);
  };

  const mostrar2 = () => {
    setIsOpen(!isOpen2);
  };


  return (
    <>
      <nav className="relative flex flex-wrap items-center justify-between bg-principal-color h-24 px-6 lg:px-12">
        <p className="text-white text-2xl">ProVision</p>

        {/* Botón de menú para pantallas pequeñas */}
       

        {/* Menú en pantallas grandes */}
        <div className="hidden lg:flex lg:space-x-8 lg:text-white">
          <a href="#" className="px-2 py-2 hover:bg-blue-600 hover:rounded-lg">Home</a>
          <a href="#" className="px-2 py-2 hover:bg-blue-600 hover:rounded-lg">About us</a>
          <a href="#" className="px-2 py-2 hover:bg-blue-600 hover:rounded-lg">Features</a>
          <a href="#" className="px-2 py-2 hover:bg-blue-600 hover:rounded-lg">Contact us</a>
        </div>

        <button
          className=" relative z-20"
          onClick={mostrar}
        >
          <img className="h-10 w-10 rounded-full" src="https://thumbs.dreamstime.com/b/l%C3%ADnea-icono-del-negro-avatar-perfil-de-usuario-121102131.jpg" alt="No Image" />
        </button>

        {isOpen && (
          <div className="absolute top-0 right-0 w-60 bg-principal-color rounded-lg shadow-lg z-30">
            <div className="text-white flex flex-col items-center pt-4 space-y-3">
              <div className='flex flex-row space-x-4 items-center justify-center'>
                <img className="h-10 w-10 rounded-full" src="https://thumbs.dreamstime.com/b/l%C3%ADnea-icono-del-negro-avatar-perfil-de-usuario-121102131.jpg" alt="No Image" />
                <p>Enterprise Name</p>
                <button onClick={mostrar}>x</button>
              </div>
              <hr className='w-full border-white border-1' />
              <a href="#" className="text-white py-2 hover:bg-blue-600 hover:rounded-lg">Your Profile</a>
              <a href="#" className="text-white py-2 hover:bg-blue-600 hover:rounded-lg">Settings</a>
              <a href="#" className="text-white py-2 hover:bg-blue-600 hover:rounded-lg">Log Out</a>
            </div>
          </div>
        )}

        {/* Menú desplegable en pantallas pequeñas */}
        {isOpen2 && (
          <div className="absolute top-0 left-0 mt-24 w-full bg-principal-color rounded-lg shadow-lg z-30 lg:hidden">
            <div className="text-white flex flex-col items-center pt-4 space-y-3">
              <a href="#" className="text-white py-2 hover:bg-blue-600 hover:rounded-lg">Home</a>
              <a href="#" className="text-white py-2 hover:bg-blue-600 hover:rounded-lg">About us</a>
              <a href="#" className="text-white py-2 hover:bg-blue-600 hover:rounded-lg">Features</a>
              <a href="#" className="text-white py-2 hover:bg-blue-600 hover:rounded-lg">Contact us</a>
            </div>
          </div>
        )}
      </nav>
    </>
  );
};

export default Navbar;
