import React, { useState } from "react";
import { useHistory } from 'react-router-dom'; // Necesario para redirigir

const Login = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const history = useHistory(); // Usado para redirigir

  const handleLogin = async (event) => {
    event.preventDefault();
    setLoading(true);

    try {
      const response = await fetch('/api/login', { // Asegúrate de ajustar la ruta de la API
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ username, password }),
      });

      if (!response.ok) {
        throw new Error(`Login failed with status: ${response.status}`);
      }

      const loginData = await response.json();

      // Puedes guardar el token en localStorage si es necesario
      localStorage.setItem('token', loginData.token);

      // Redirigir a la página de inicio
      history.push('/Home'); // Redirigir a la página de inicio

    } catch (error) {
      setError(error.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="bg-principal-color h-screen flex flex-col md:flex-row items-center justify-center">
      <div className="h-60 w-full md:h-96 md:w-3/5 flex-shrink-0">
        <img
          className="h-full w-full object-cover"
          src="https://wallha.com/download/ws/13/fotYTdx4-wallha.com.jpg"
          alt="Background"
        />
      </div>

      <div className="h-auto w-full md:h-96 md:w-80 bg-white flex flex-col items-center p-6">
        <p className="text-sm mt-4">ProVision</p>
        <p className="text-3xl font-serif mt-2">Bienvenido de Nuevo</p>
        <img
          className="h-14 w-14 mt-2 rounded-full border-b-2 border-black"
          src="https://thumbs.dreamstime.com/b/l%C3%ADnea-icono-del-negro-avatar-perfil-de-usuario-121102131.jpg"
          alt="User Avatar"
        />

        <div className="flex flex-col space-y-2 mt-5">
          <p className="text-xs">USER</p>
          <input
            type="text"
            className="border-b-2 border-black w-full md:w-52 focus:outline-none"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
          <p className="text-xs">PASSWORD</p>
          <input
            type="password"
            className="border-b-2 border-black w-full md:w-52 focus:outline-none"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </div>

        <div className="flex flex-col md:flex-row space-y-2 md:space-y-0 md:space-x-6 mt-4">
          <div className="flex items-center space-x-2">
            <input type="checkbox" id="rememberMe" className="h-4 w-4" />
            <label htmlFor="rememberMe" className="text-xs">Remember me</label>
          </div>
          <a href="#" className="text-xs underline hover:text-gray-600">Forgot Password?</a>
        </div>

        <button
          onClick={handleLogin}
          className="mt-6 bg-principal-color rounded-md text-white px-6 py-1 hover:bg-blue-600"
          disabled={loading}
        >
          {loading ? 'Loading...' : 'Login'}
        </button>

        {error && <div className="mt-4 text-red-500 text-sm">{error}</div>}
      </div>
    </div>
  );
};

export default Login;
